mapper.select:
select sli.id, sli.school_id, sli.discipline_code, sli.`type`, sli.score, sli.template_flag, sli.`year`, sli.admissions, ss.name as schoolName, ss.address, ss.city, ss.province, ss.manage, ss.`level`, di.name as disciplineName, di.employment from score_line_info sli left join sys_school ss on (ss.id = sli.school_id) left join discipline_info di on (di.code = sli.discipline_code) where 1 = 1 AND sli.template_flag = '0'
AND ss.name LIKE CONCAT('%',#{scoreLineInfo.schoolName},'%')
AND di.name LIKE CONCAT('%',#{scoreLineInfo.disciplineName},'%')
AND sli.type = #{scoreLineInfo.type}
AND sli.year = #{scoreLineInfo.year}
AND ss.user_id = #{scoreLineInfo.schoolId}
order by sli.`year` desc

select sli.id, sli.school_id, sli.discipline_code, sli.`type`, sli.score, sli.template_flag, sli.`year`, sli.admissions, ss.name as schoolName, ss.city, ss.province, ss.manage, ss.`level`, di.name as disciplineName, di.employment, di.remark from score_line_info sli left join sys_school ss on (ss.id = sli.school_id) left join discipline_info di on (di.code = sli.discipline_code) where 1 = 1 AND sli.school_id = #{schoolId} AND sli.template_flag = '0'
AND sli.year = #{year}
order by sli.`year` desc

select sli.id, sli.school_id, sli.discipline_code, sli.`type`, sli.score, sli.template_flag, sli.`year`, sli.admissions, ss.name as schoolName, ss.area, ss.manage, ss.`level`, di.name as disciplineName, di.employment from score_line_info sli left join sys_school ss on (ss.id = sli.school_id) left join discipline_info di on (di.code = sli.discipline_code) where 1 = 1
AND sli.school_id in
#{id}
AND sli.template_flag = '0'
AND sli.year = #{year}
order by sli.`year` desc
